mapper.select:
and ( messages like concat("%",#{search},"%") or mobile like concat("%",#{search},"%") or email like concat("%",#{search},"%") or weixin like concat("%",#{search},"%") or ding like concat("%",#{search},"%") or b.ip_address like concat("%",#{search},"%") )
and messages_time >= #{startT} and messages_time <= #{endT}
order by messages_id desc
