mapper.select:
select

from

where a.groups_id = b.groups_id
and a.type_id = c.type_id
and a.os_id = d.os_id
and a.cabinet_id = e.cabinet_id
and a.user_id = f.user_id
and a.service_id = g.service_id
and a.host_id = h.server_id
and a.ent_id = i.ent_id


    and c.type_name=#{typeName}




    and a.server_id=#{hostId}




    and substr(from_unixtime(a.create_time,"%Y-%m-%d %H:%i:%S"), 1,7) = #{time}




    and i.ent_name  = #{entName}





    and e.cabinet_scope = #{cabinetScope}




    and b.groups_name=#{groupsName}




    and f.user_name=#{userName}




    and e.cabinet_name=#{cabinetName}




    and a.host_id=#{vmHostId}




    and d.os_name=#{osName}




    and a.status = 0




    and (a.ip_address like concat('%',#{search},'%')
    or f.user_name like concat('%',#{search},'%')
    or a.domain_name like concat('%',#{search},'%')
    or b.groups_name like concat('%',#{search},'%')
    or a.manager_ip like concat('%',#{search},'%')
    or a.asset_coding like concat('%',#{search},'%')
    )





    and a.host_id=h.server_id
    and a.host_id != a.server_id
    and h.ip_address=#{hostIp}




    and a.server_id in

        #{item}




    and a.ip_address=#{ipAddress}

ORDER BY a.create_time DESC

select
from
where a.groups_id = b.groups_id and a.type_id = c.type_id and a.os_id = d.os_id and a.cabinet_id = e.cabinet_id and a.user_id = f.user_id and a.service_id = g.service_id and a.host_id = h.server_id and a.ent_id = i.ent_id and a.server_id=#{serverId}

select count(*) as cnt from cmdb_resource_server where groups_id in

#{item}


    and create_time >= #{start}

  select groups_name as groupsName,count(*) as cnt from
   cmdb_resource_server a, cmdb_resource_groups b
    where a.groups_id=b.groups_id and
    a.groups_id in   
   #{item}

group by groupsName;

select server_id as serverId,
ip_address as ipAddress,
a.groups_id as groupsId
from cmdb_resource_server a
where 1=1

       and ip_address=#{ipAddress}


        and a.groups_id = #{groupsId}


        and ent_id = #{entId}

mapper.update:
update cmdb_resource_server set
    status = #{status}
    WHERE
    ip_address in

        #{item}

mapper.select:
select b.user_name as userName, count(a.groups_id) as cnt from cmdb_resource_server a, cmdb_resource_user b, cmdb_resource_entname c, cmdb_resource_groups d where a.user_id=b.user_id and a.ent_id = c.ent_id and a.groups_id=d.groups_id
and d.groups_name = #{groupsName}
and c.ent_name = #{entName}
group by b.user_name order by cnt desc

select count(*) as cnt , ent_name as entName from cmdb_resource_server a , cmdb_resource_entname b, cmdb_resource_groups c where a.ent_id=b.ent_id and a.groups_id = c.groups_id
and c.groups_name = #{groupsName}
group by ent_name order by cnt desc

select count(*) as cnt , service_name as serviceName from cmdb_resource_server a , cmdb_resource_service b, cmdb_resource_groups c where a.service_id=b.service_id and a.groups_id = c.groups_id
and c.groups_name = #{groupsName}
group by service_name

select count(*) as cnt , groups_name as groupsName from cmdb_resource_server a , cmdb_resource_groups b, cmdb_resource_entname c where a.groups_id=b.groups_id and a.ent_id=c.ent_id
and c.ent_name = #{entName}
group by groups_name order by cnt desc

select count(*) as cnt , type_name as typeName from cmdb_resource_server a , cmdb_resource_server_type b, cmdb_resource_groups c where a.type_id=b.type_id and a.groups_id=c.groups_id
and c.groups_name=#{groupsName}
group by type_name order by cnt desc

select b.type_name as typeName, count(a.type_id) as cnt from cmdb_resource_server a, cmdb_resource_server_type b, cmdb_resource_groups c where a.type_id=b.type_id and a.groups_id = c.groups_id
and c.groups_name=#{groupsName}
group by b.type_name

select b.service_name as typeName, count(a.service_id) as cnt from cmdb_resource_server a, cmdb_resource_service b, cmdb_resource_groups c where a.service_id=b.service_id and a.groups_id = c.groups_id
and c.groups_name=#{groupsName}
group by b.service_name

select b.groups_name as typeName, count(a.groups_id) as cnt from cmdb_resource_server a, cmdb_resource_groups b, cmdb_resource_entname c where a.groups_id=b.groups_id and a.ent_id = c.ent_id
and c.ent_name=#{entName}
group by b.groups_name order by cnt desc
